
$(document).ready(function () {

      $(".login-form-grids").trigger('load'); // When page is loaded we trigger a click

});
/**
 * Cette partie concerne les formulaires publications
 */
$(".login-form-grids").load(function () {

/**
 *
 * Code pour la connexion
 */


$("#loginform").submit(function () {
    var pseudo=$("#loginpseudo").val();
    var pass=$("#loginpass").val();
    if(pseudo.trim().length==0){
        $("#alert-login").html('<b>Le champ pseudo est obligatoire !</b>').fadeIn(400);
    }
    else if(pass.length==0){
        $("#alert-login").html('<b>Le champ mot de passe est obligatoire !</b>').fadeIn(400);
    }
    else{
        connect(pseudo,pass);
    }

});

//fonction pour la connexion
function connect(pseudo,pass){
    $.ajax({
        type:"post",
        url:"controls/connect.php",
        data:{
            'pseudo':pseudo,
            'pass':pass

        },
        success:function(data){
            if(data=='success'){
                setTimeout('window.location.href= "index.php";');
            }
            else{
                $("#alert-login").html(data);
            }
        }

    });
}

});